content.css:
.gantt-container {
  width: 100%;
  height: 100%;
  overflow-x: hidden;
  overflow-y: auto;
  font-family: "Segoe UI Variable", "Segoe UI", -apple-system, BlinkMacSystemFont, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  box-sizing: border-box;
}
.gantt-svg {
  display: block;
  max-width: 100%;
}
.gantt-message {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  color: #616161;
  font-size: 14px;
  font-weight: 400;
  text-align: center;
  padding: 20px;
}
.gantt-landing-page {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  padding: 32px;
  text-align: center;
  color: #424242;
  box-sizing: border-box;
}
.gantt-landing-icon {
  color: #0f6cbd;
  margin-bottom: 16px;
}
.gantt-landing-icon svg {
  display: block;
}
.gantt-landing-title {
  font-size: 24px;
  font-weight: 600;
  color: #242424;
  margin: 0 0 16px 0;
}
.gantt-landing-instructions {
  text-align: left;
  max-width: 480px;
  font-size: 13px;
  line-height: 1.5;
}
.gantt-landing-instructions p {
  margin: 0 0 12px 0;
  color: #424242;
}
.gantt-landing-instructions ul {
  margin: 0;
  padding-left: 20px;
}
.gantt-landing-instructions ul li {
  margin-bottom: 8px;
  color: #424242;
}
.gantt-landing-instructions ul li strong {
  color: #242424;
  font-weight: 600;
}
.gantt-landing-instructions ul li .required {
  color: #0f6cbd;
  font-size: 11px;
  font-weight: 500;
}
.gantt-landing-instructions ul li .optional {
  color: #616161;
  font-size: 11px;
}
.gantt-landing-features {
  margin-top: 20px;
  padding-top: 16px;
  border-top: 1px solid #e0e0e0;
}
.gantt-landing-features .features-hint {
  font-size: 12px;
  color: #616161;
  margin: 0;
}
.gantt-landing-version {
  margin-top: 16px;
  font-size: 11px;
  color: #616161;
  opacity: 0.7;
}
.gantt-svg {
  display: block;
}
.gantt-header {
  fill: #242424;
  font-size: 14px;
  font-weight: 600;
  text-anchor: middle;
}
.gantt-row-even {
  fill: #fafafa;
}
.gantt-row-odd {
  fill: #ffffff;
}
.gantt-task-label {
  fill: #242424;
  font-size: 12px;
  font-weight: 400;
}
.gantt-bar {
  fill: #0f6cbd;
  cursor: pointer;
  transition: all 0.1s ease-out;
}
.gantt-bar:hover {
  fill: #115ea3;
  filter: drop-shadow(0 1px 2px rgba(0, 0, 0, 0.14), 0 0 2px rgba(0, 0, 0, 0.12));
}
.gantt-bar:active {
  fill: #0e4775;
}
.gantt-bar-label {
  fill: #ffffff;
  font-size: 11px;
  font-weight: 600;
  text-anchor: middle;
  pointer-events: none;
}
.gantt-axis-label {
  fill: #616161;
  font-size: 10px;
  font-weight: 400;
  text-anchor: middle;
}
.gantt-axis-line {
  stroke: #e0e0e0;
  stroke-width: 1;
}
.gantt-grid-line {
  stroke: #e0e0e0;
  stroke-width: 1;
}
.gantt-separator {
  stroke: #d1d1d1;
  stroke-width: 1;
}
.gantt-milestone {
  fill: #c4314b;
  cursor: pointer;
  transition: all 0.1s ease-out;
}
.gantt-milestone:hover {
  filter: brightness(1.1) drop-shadow(0 1px 2px rgba(0, 0, 0, 0.14), 0 0 2px rgba(0, 0, 0, 0.12));
}
.gantt-selected {
  stroke: #242424 !important;
  stroke-width: 2px !important;
  stroke-dasharray: none !important;
  filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.14), 0 0 2px rgba(0, 0, 0, 0.12));
}
.gantt-label-highlight {
  pointer-events: none;
}
.gantt-category-header {
  cursor: pointer;
}
.gantt-category-header:hover {
  opacity: 0.9;
}
.gantt-dependency-line {
  fill: none;
}
.critical-path-toggle {
  transition: opacity 0.15s ease-out;
}
.critical-path-toggle:hover {
  opacity: 0.85;
}
.critical-path-toggle:active {
  opacity: 0.7;
}
.gantt-bar:focus,
.gantt-task-label:focus,
[role="button"]:focus {
  outline: 2px solid #0f6cbd;
  outline-offset: 2px;
}
@media (forced-colors: active) {
  .gantt-bar:focus,
  .gantt-task-label:focus,
  [role="button"]:focus {
    outline: 3px solid CanvasText;
    outline-offset: 2px;
  }
}
