content.css:
.cards-grid {
  width: 100%;
  height: 100%;
  box-sizing: border-box;
  display: grid;
  overflow: auto;
  padding: 10px;
  align-content: start;
  grid-auto-rows: min-content;
}
.card-container {
  box-sizing: border-box;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  border-radius: 12px;
  display: flex;
  flex-direction: column;
  min-width: 200px;
  height: auto;
}
.card-container .header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  flex-wrap: wrap;
  gap: 8px;
  margin: 0;
  padding: 0;
  line-height: 1.2;
}
.card-container .header .title {
  font-size: 24px;
  color: #333;
  font-weight: 400;
  margin: 0;
  padding: 0;
  line-height: 1.2;
}
.card-container .header .badge {
  padding: 8px 20px;
  border-radius: 25px;
  font-size: 18px;
  font-weight: 600;
  white-space: nowrap;
  margin: 0;
  line-height: 1.2;
}
.card-container .main-value {
  font-size: 72px;
  font-weight: 700;
  color: #1a1a1a;
  word-break: break-word;
  margin: 0;
  padding: 0;
  line-height: 1;
}
.card-container .details {
  font-size: 20px;
  flex-wrap: wrap;
  margin: 0;
  padding: 0;
  line-height: 1.2;
}
.card-container .details .target-label {
  font-weight: 700;
  color: #333;
}
.card-container .details .target-value {
  color: #666;
}
.card-container .details .separator {
  margin: 0 8px;
  color: #333;
}
.card-container .details .exceeded-label {
  font-weight: 700;
  color: #333;
}
.card-container .details .exceeded-value {
  font-weight: 400;
}
.card-container .progress-container {
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: column;
}
.card-container .progress-container .progress-bar {
  position: relative;
  height: 16px;
  border-radius: 10px;
  overflow: visible;
  margin-bottom: 10px;
}
.card-container .progress-container .progress-bar .progress-fill {
  position: absolute;
  top: 0;
  left: 0;
}
.card-container .progress-container .progress-bar .marker {
  position: absolute;
  top: -4px;
  width: 4px;
  height: 24px;
  background-color: #000;
  border-radius: 2px;
  transform: translateX(-50%);
}
.card-container .progress-container .progress-label {
  margin-top: 8px;
  font-size: 18px;
  color: #333;
}
